import { InputText } from 'primereact/inputtext';
import { Divider } from 'primereact/divider';
import { Dropdown } from 'primereact/dropdown';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';
import React from 'react'
import useGlobalState from '../globalState';
import { InputTextarea } from 'primereact/inputtextarea'
import { useState, useEffect } from 'react';
import getColaborador from '../Services/getColaborador';
import GLOBAL from '../integration/storage.json'
import { InputNumber } from 'primereact/inputnumber';

export default function Tarefa_1() {
    const { globalState } = useGlobalState()
    const info = globalState.variaveisProcesso

    const motivoSolicitacao = [
        { label : 'Alteração de Unidade', cod :1 },
        { label : 'Alteração de Escala', cod :2 },
        { label : 'Alteração de Endereço', cod :3 },
        { label : 'Outros (Justifique)', cod :4 },

    ]

    //ver useState
    const [dados, setDados] = useState(null)

    const [beneficios, setBeneficios] = useState(
        info?.beneficios_JSON.beneficios || 
    [
        {
            tipoBeneficio: ''
        }
    ])

    const [motivoSelecionado, setMotivoSelecionado]=useState(null)

    //ver useEffect
    useEffect(() => {
        getColaborador(globalState.usuario.subject).then((param) => {
            setDados(param)
        })
    }, [])


    useEffect(() => {
        GLOBAL.tarefa_1 = {
            ...dados,
            beneficios
        }
    })
    
    // Funçao para selecionar os beneficios na lista de beneficios
    function selectTypes(e, i) {
        let beneficiosCopy = Array.from(beneficios)
        beneficiosCopy[i].tipoBeneficio = e.value
        setBeneficios(beneficiosCopy)
    }

    //Cria uma copia de beneficios e atualiza
    function adicionarBeneficios() {
        let beneficiosCopy = Array.from(beneficios)
        beneficiosCopy.push({
            tipoBeneficio: ''
        })
        setBeneficios(beneficiosCopy)
    }

    function onSelect(param, i) {
        if (param === 1) {
            return (<>
                <div className="col-12 field">
                    {console.log(beneficios)}
                    <label>
                        Nome da operadora de Transporte
                    </label>
                    <InputText
                        className="w-full"
                        value={beneficios[i]?.nomeOperadora}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].nomeOperadora = e.target.value)}
                    />

                </div>
                <div className="col-12 field">

                    <label>
                        Número da Linha
                    </label>
                    <InputNumber className="w-full" inputClassName="w-full"
                        value={beneficios[i]?.numeroLinha}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].numeroLinha = e.value)}
                    />
                </div>
                <div className="col-12 field">

                    <label>
                        Tipo de Transporte
                    </label>
                    <InputText className="w-full"
                        value={beneficios[i]?.tipoTransporte}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].tipoTransporte = e.target.value)}
                        placeholder="Onibus, metro, trem etc"
                    />
                </div>
                <div className="col-12 field">

                    <label>
                        Valor da Tarifa
                    </label>
                    <InputNumber className="w-full" inputClassName="w-full" mode="currency" currency="BRL" locale="pt-BR"
                        value={beneficios[i]?.valorTarifa}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].valorTarifa = e.value)}
                    />

                </div>
                <div className="col-12 field">

                    <label>
                        Quantidade utilizada para ida
                    </label>
                    <InputNumber className="w-full"
                        value={beneficios[i]?.quantidadeIda}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].quantidadeIda = e.value)}
                    />

                </div>
                <div className="col-12 field">

                    <label>
                        Quantidade utilizada para volta
                    </label>
                    <InputNumber className="w-full"
                        value={beneficios[i]?.quantidadeVolta}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].quantidadeVolta = e.value)}
                    />

                </div>
            </>
            )

        } else if (param === 2) {

            const listaTipoPessoa = [
                { label: 'Titular' },
                { label: 'Dependente' },

            ]

            return (<>
                <div className="col-12 field">
                    {console.log(beneficios)}
                    <label>
                        Tipo
                    </label>
                    <Dropdown options={listaTipoPessoa} className="w-full"
                        value={beneficios[i]?.tipoPessoa}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].tipoPessoa = e.value)}
                    />

                </div>
                {
                    beneficios[i].tipoPessoa?.label === "Dependente" &&
                    <div className="col-12 field">
                        <Dropdown
                            className="w-full"
                            optionLabel="nome"
                            options={[
                                { nome: "Dependente1" },
                                { nome: "Dependente2" }
                            ]}
                            onChange={(e) => setBeneficios([...beneficios], beneficios[i].dependente = e.value)}
                            value={beneficios[i]?.dependente}
                        />
                    </div>
                }
                <div className="col-12 field">

                    <label>
                        Plano
                    </label>
                    <Dropdown
                        className="w-full"
                        optionLabel="nome"
                        options={[
                            { nome: "Plano1" },
                            { nome: "Plano2" }
                        ]}
                        onChange={(e) => setBeneficios([...beneficios], beneficios[i].tipoPlano = e.value)}
                        value={beneficios[i]?.tipoPlano}
                    />
                </div>

            </>)

        } else if (param === 3) {
            return " "
        }
    }

    return (
        <div className="grid">
            {
                globalState.variaveisProcesso?.observacao &&
                <div className="col-12 field">
                    <label>
                        Observação CSC
                    </label>
                    <InputTextarea className="w-full"
                        value={info.observacao}
                        readonly
                    />
                </div>
            }
            <div className="col-12 field">
                <label>
                    COLABORADOR
                </label>
                <InputText className="w-full"
                    value={dados?.usuario.nomFun}
                    readonly
                />
            </div>
            {/* <Divider align="left" >Histórico Anterior </Divider> */}

            <div className="col-12 field">

                <label>
                    Empresa \ Filial
                </label>
                <InputText className="w-full" value={dados?.empresa.numEmp + '-' + dados?.empresa.codFil +  '-' + dados?.empresa.nomFil } 
                    readonly
                />
            {console.log(dados)}
            </div>

            <Divider align="left" > BENEFÍCIOS </Divider>
            
                <div className="col-12 field">
                    <Dropdown 
                    value={motivoSelecionado}
                    options={motivoSolicitacao} 
                    className="w-full"
                    onChange={(e)=> setMotivoSelecionado(e.value)}
                    />
                </div>            

            <Divider align="left" > Justificativa </Divider>
            <div className="col-12 field">
                <label>
                    Descrição
                </label>
                <InputTextarea className="w-full" rows={4} value={dados?.descricao} onChange={(e) => setDados({ ...dados, descricao: e.target.value })} />
            </div>
        </div>
    )
}
