    //                     ]}
    //                     onChange={(e) => setBeneficios([...beneficios], beneficios[i].tipoPlano = e.value)}
    //                     value={beneficios[i]?.tipoPlano}
    //                 />
    //             </div>

    //         </>)

    //     } else if (param === 3) {
    //         return " "
    //     }
    // }

    return (
        <div className="grid">
            {
                globalState.variaveisProcesso?.observacao &&
                <div className="col-12 field">
                    <label>
                        Observação CSC
                    </label>
                    <InputTextarea className="w-full"
                        value={info.observacao}
                        readonly
                    />
                </div>
            }
            <div className="col-12 field">
                <label>
                    COLABORADOR
                </label>
                <InputText className="w-full"
                    value={dados?.usuario.nomFun}
                    readonly
                />
            </div>

            <div className="col-12 field">

                <label>
                    Empresa \ Filial
                </label>
                <InputText className="w-full" value={dados?.empresa.numEmp + '-' + dados?.empresa.codFil + '-' + dados?.empresa.nomFil}
                    readonly
                />
            </div>

            <Divider align="left" > BENEFÍCIOS </Divider>

            <div className="col-12 field">
                <Dropdown
                    value={motivoSelecionado}
                    options={motivoSolicitacao}
                    className="w-full"
                    onChange={(e) => setMotivoSelecionado(e.value)}
                />
            </div>

            <Divider align="left" > Outros </Divider>
            <div className="col-12 field">
                <label>
                    Justificativa
                </label>
                <InputTextarea className="w-full" rows={4} value={dados?.descricao} onChange={(e) => setDados({ ...dados, descricao: e.target.value })} />
            </div>

            <Divider align="left" > Escolha o beneficio. </Divider>

            <div className="col-12 field">
                <Dropdown
                    value={beneficioSelecionado}
                    options={beneficio}
                    className="w-full"
                    onChange={(e) => setBeneficioSelecionado(e.value)}
                />
            </div>



            {/* Vale Transporte */}

            <div className="col-12 field">
                <label>
                    Escala Vale de transporte Atual
                </label>
                <InputText className="w-full" value={"Exemplo"}
                    readonly
                />
            </div>
            <label>Tipo de operação</label>

            <div className="col-12 field">
                <Dropdown
                    value={operacaoSelecionada}
                    options={operacao}
                    className="w-full"
                    onChange={(e) => setOperacaoSelecionada(e.value)}
                />
            </div>

            <div className="col-12 field">
                <label>
                    Nome da Operadora de Vale Transporte
                </label>
                <InputText className="w-full" value={"Empresa XPTO"}
                    readonly
                />
            </div>

            <div className="col-12 field">
                <label>
                    Numero da Linha
                </label>
                <InputNumber className="w-full" value={999999}
                    readonly
                />
            </div>

            <div className="col-12 field">
                <label>
                    Tipo de Transporte
                </label>
                <InputText className="w-full" value={'Onibus,Metro,Trem,Etc.'}
                    readonly
                />
            </div>
            <div className="col-12 field">
                <label>
                    Valor da Tarifa
                </label>
                <InputNumber className="w-full" value={99.99}
                    readonly
                />
            </div>
            <div className="col-12 field">
                <label>
                    Quantidade Utilizada para Ida
                </label>
                <InputNumber className="w-full" value={1}
                    readonly
                />
